package com.syntax.class01;

public class HomeWorkVariableValues {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
	// Numeric Values
		byte file = 10;
		short city = 28543;
		int mikie = 104655;
		long disney = 841254112l;
		
		// decimal numbers
		
		float boat = 9.0f;
		double park = 11.99;
		
		// single characters
		
		char luna ='%';
		char lucky ='@';
		char money='$';
		
		// yes/ no characters
		
		boolean love = true;
		boolean rich = false;
		
		System.out.println (file);
		
		System.out.println (city);
		
		System.out.println (mikie);
		
		System.out.println (disney);
		
		System.out.println (file + city + mikie + disney);
		
		System.out.println (boat + park);
		
		System.out.println (luna);
		
		System.out.println (lucky);
		
		System.out.println (money);
		
		System.out.println (love);
		
		System.out.println (" its snowing outside?" + rich);
		

	}

}
