package com.syntax.class01;

public class PrimitiveDataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	
//storing and representing numeric values
	byte box1 = 127;
	short box2 = 32767;
	int box3 = 100000; //the most popular numeric value
	long box4 = 100000564000l;
	
	
	//storing and representing Decimal numbers
	float variable1 = 999.999f;
	double variable2 = 99.1; //popular
	
	//storing and representing single characters
	 
	char container ='A';
	char container1 ='$';
	char dollar = '&';
	
	//storing and representing yes or no characters
	boolean hot =true;
	boolean tired = false;

	System.out.println (box1); //printing value of the variable
	System.out.println(box2);
	System.out.println(box3);
	System.out.println(box4);
	System.out.println(variable1);
	System.out.println(variable2);
	System.out.println(container1);
	System.out.println ("dollar"); // printing text variable
	System.out.println(tired);
	System.out.println(hot);

	
	
	}
}
