package com.syntax.class02;

public class ArithmeticOperations {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
         
		//+,-,*,/,%
		
		
		int a =250;
		int b =25;
		System.out.println(a+b);
		System.out.println(a-b);
		System.out.println(b*a);
		System.out.println(a/b);
		
		System.out.println(" The sum of 2 numbers "+ a+" and " +b +" is equal to " + (a+b));
		System.out.println(" The difference of 2 numbers "+ a+" and " +b +" is equal to " + (a-b));
		System.out.println(" The product of 2 numbers "+ a+" and " +b +" is equal to " + (a*b));
		System.out.println(" The divident of 2 numbers "+ a+" and " +b +" is equal to " + (a/b));
		System.out.println(" The remainder of 2 numbers "+ a+" and " +b +" is equal to " + (a%b));

		
		// Square a number
		
	float power = 3.9f;
	System.out.println(" The square of the "+ power +" is " + power*power);
	
	    
	    // finding the Perimeter
	
	    int heigh = 8;
	    int width = 5;
	    System.out.println(" The perimeter of a rectangle with width "+ width+" and height " + heigh+" is equal to "+ ((8*2)+(5*2)));
	    
	    // finding the Area
	    
	    System.out.println(" The area of a rectangle with width "+ width+" and height " + heigh+" is equal to "+(8*5));
	    System.out.println(" The perimeter of a rectangle with width "+ width+" and height " + heigh+" is equal to "+ ((8*2)+(5*2))+ " and the area is equal to "+(8*5));

		
	}

}
