package com.syntax.class02;

public class RulesIndentifierNamesVariables {

	public static void main(String[] args) {
		
		// TODO Auto-generated method stub
		/*
		 *  1. cannot use keywords as identifiers
		 // String new="Hello"; ->>> error
		
		// 2. cannot have spaces in identifiers 
		// String last name="Smith" ->>> error -> lastName
		
		//3. identifiers cannot start with numbers
		// int 1number=123 ->> error -->>> number1 // number can be anywhere after alphabetically name
		
		/*4. identifiers cannot contain any special characters EXCEPT
		 */
		// $ or _
		
		//boolean  hello!= true; ->>error
		 
		 boolean hello$ = true;
		 float _prices = 1.99f;
		 double $price1 = 11.99;
		 
		 
		 /*
		  * Naming Conventions
		  * Class should start with Uppercase and Followed by CamelCASING
		  * variable should start with lower cases then start with CamelCasing
		  */
		 String lastName = "Cantir";
		 String myWeather="cold";
		 
	}

}
