package com.syntax.class03;

public class Recap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*
		 * String Concatenation help us to combine 2-3 string together
		 */
		String firstName="Zina";
		String lastName="Cantir";
		String fullName=firstName+ " " + lastName;
		System.out.println(fullName);
		
		int houseNo=10;
		int apartmentNo=10;
		String streetName ="Poplar ave";
		String city ="New York";
		String country="USA";
		String fullAddress=apartmentNo+" "+streetName+" "+ houseNo+" "+ city+" "+ " "+ country;
		System.out.println(fullAddress);
		
		
		
	}

}

