package com.syntax.class12;

public class StringDemo1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String str = "dna";

		/*
		 * if we need to convert smth from lowerCase to Upper Case we can use this
		 * method
		 */
		System.out.println(str.toUpperCase()); // this method will convert the lower to Upper case

		str = "JAVA";
		System.out.println(str.toLowerCase());

	}

}
