package com.syntax.class8Loops;

public class LoopHwEvenNum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int e =20;
		while (e>=1) {
			System.out.print(e+" ");
			e-=2;
		}
		System.out.println("----------------------------");
		int even =20;
		while (even>=1) {
			if(even%2==0)
			System.out.print(even + " ");
			even-=2;
		
	}

}
}
