package com.syntax.class8Loops;

public class LoopOddNumb100to1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int odd =100;
		while (odd>=1) {
			if(odd%2==1)
			System.out.print(odd + " ");
			odd--;
		}
		 int e =99;
		 while (e>=1) {
			 System.out.println
			 
			 (e+ " ");
			 e-=2;
	}

}
}
