package com.syntax.class8Loops;

public class WhileLoopDem2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// print all numbers 1 to 10
		 
	int num=1;

		while (num<=10) {   //its a code in Java -->>> Loop -->repeat this block of code
		 System.out.print (num + " "); 
		/*
		 * to control my infinite loop we are using increment/decrement
		 */
		num ++;
		
		///////////print all number from 10 to 25
		int a=10;
		
	while (a<=25) {   //its a code in Java -->>> Loop -->repeat this block of code
			
			 System.out.print(a + " "); 
			 
			 a++;
			 
///////////////////////////// print all number from 10 to 1 
			 int b=10;
			 while (b>=1) {
			 System.out.println(b+ " ");
			 b--;
	 }
			 
// print all number from 50 to 20
			 
// print all even numbers from 1 to 20
			 
			 int e =50;
			 while (e>=20) {
				 System.out.println(e+ " ");
				 e-=2;
			 }
			 
			 

	}

}
	}
}
