package com.syntax.class8NestedLoop;

public class NesteLoopClock {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for (int h=01; h<=23; h++) {
			
			for (int m=01; m<=59; m++) {
								
					System.out.println(h + ":"+m );
			}
				System.out.println("------------------------------------");
		}
		}
	}


