package com.syntax.class9Arrays;

public class ArrayDemo2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String [] drinks= {"fanta", "milk","juice","lemonade","water","drPepper"};
		
		System.out.println(drinks[2]); //juice
		
		//how many elements inside my array?
		
		int size =drinks.length; //====>>>>> this will give number of elements in the array
		
		System.out.println("Size of array = "+ size);
	}

}
